INSERT INTO `mn_news` VALUES (167,'Ốp sàn nhựa giả gỗ lamdri','','<p><span class=\"_5mfr _47e3\" style=\"line-height: 0; vertical-align: middle; margin: 0px 1px; font-family: Helvetica, Arial, sans-serif;\"><img alt=\"\" class=\"img\" height=\"16\" role=\"presentation\" src=\"https://static.xx.fbcdn.net/images/emoji.php/v9/fdf/1/16/1f468_200d_1f527.png\" style=\"border: 0px; vertical-align: -3px;\" width=\"16\" /></span><span style=\"font-family: Helvetica, Arial, sans-serif;\">Ốp s&agrave;n nhựa giả gỗ lamdri l&agrave;m nổi bật bề mặt tường cho ph&ograve;ng kh&aacute;ch, văn ph&ograve;ng c&ocirc;ng ty, sảnh kh&aacute;ch sạn một c&aacute;ch tinh tế - thẩm mỹ cao.</span></p>\r\n','','<h5 class=\"_14f3 _14f5 _5pbw _5vra\" data-ft=\"{\" id=\"js_l\" style=\"box-sizing: border-box; font-family: inherit; line-height: 1.38; color: rgb(50, 60, 63); margin: 0px 0px 2px; font-size: 14px; letter-spacing: 0.01em; text-transform: uppercase; padding: 0px 22px 0px 0px;\"><span class=\"_5mfr _47e3\" style=\"box-sizing: border-box; line-height: 0; vertical-align: middle; margin: 0px 1px; font-family: Helvetica, Arial, sans-serif; color: rgb(29, 33, 41);\"><img alt=\"\" class=\"img\" height=\"16\" role=\"presentation\" src=\"https://static.xx.fbcdn.net/images/emoji.php/v9/ff4/1/16/2728.png\" style=\"box-sizing: border-box; border: 0px; vertical-align: -3px; max-width: 100%; height: auto; max-height: 100%;\" width=\"16\" /><span class=\"_7oe\" style=\"box-sizing: border-box; display: inline; font-size: 0px; width: 0px; font-family: inherit;\">✨</span></span><span style=\"box-sizing: border-box; color: rgb(29, 33, 41); font-family: Helvetica, Arial, sans-serif;\">PHAN RANG DESIGN</span><span class=\"_5mfr _47e3\" style=\"box-sizing: border-box; line-height: 0; vertical-align: middle; margin: 0px 1px; font-family: Helvetica, Arial, sans-serif; color: rgb(29, 33, 41);\"><img alt=\"\" class=\"img\" height=\"16\" role=\"presentation\" src=\"https://static.xx.fbcdn.net/images/emoji.php/v9/ff4/1/16/2728.png\" style=\"box-sizing: border-box; border: 0px; vertical-align: -3px; max-width: 100%; height: auto; max-height: 100%;\" width=\"16\" /><span class=\"_7oe\" style=\"box-sizing: border-box; display: inline; font-size: 0px; width: 0px; font-family: inherit;\">✨</span></span><br style=\"box-sizing: border-box; color: rgb(29, 33, 41); font-family: Helvetica, Arial, sans-serif;\" />\r\n<span class=\"_5mfr _47e3\" style=\"box-sizing: border-box; line-height: 0; vertical-align: middle; margin: 0px 1px; font-family: Helvetica, Arial, sans-serif; color: rgb(29, 33, 41);\"><img alt=\"\" class=\"img\" height=\"16\" role=\"presentation\" src=\"https://static.xx.fbcdn.net/images/emoji.php/v9/fdf/1/16/1f468_200d_1f527.png\" style=\"box-sizing: border-box; border: 0px; vertical-align: -3px; max-width: 100%; height: auto; max-height: 100%;\" width=\"16\" /></span><span style=\"box-sizing: border-box; color: rgb(29, 33, 41); font-family: Helvetica, Arial, sans-serif;\">ỐP S&Agrave;N NHỰA GIẢ GỖ LAMDRI L&Agrave;M NỔI BẬT BỀ MẶT TƯỜNG CHO PH&Ograve;NG KH&Aacute;CH, VĂN PH&Ograve;NG C&Ocirc;NG TY, SẢNH KH&Aacute;CH SẠN MỘT C&Aacute;CH TINH TẾ - THẨM MỸ CAO.</span><br style=\"box-sizing: border-box; color: rgb(29, 33, 41); font-family: Helvetica, Arial, sans-serif;\" />\r\n<span class=\"_5mfr _47e3\" style=\"box-sizing: border-box; line-height: 0; vertical-align: middle; margin: 0px 1px; font-family: Helvetica, Arial, sans-serif; color: rgb(29, 33, 41);\"><img alt=\"\" class=\"img\" height=\"16\" role=\"presentation\" src=\"https://static.xx.fbcdn.net/images/emoji.php/v9/f87/1/16/1f340.png\" style=\"box-sizing: border-box; border: 0px; vertical-align: -3px; max-width: 100%; height: auto; max-height: 100%;\" width=\"16\" /></span><span style=\"box-sizing: border-box; color: rgb(29, 33, 41); font-family: Helvetica, Arial, sans-serif;\">THI C&Ocirc;NG S&Agrave;N NHỰA CHUY&Ecirc;N NGHIỆP THEO THIẾT KẾ HIỆN ĐẠI, N&Acirc;NG CAO GI&Aacute; TRỊ SANG TRỌNG CHO NỘI THẤT CĂN NH&Agr